    <!DOCTYPE html PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN" "http://www.w3.org/TR/html4/loose.dtd"><html>	<head>		<?php 		//Get Database				require ('includes/db_connect.php');						//Star Session for base security				session_start();			?>		<meta http-equiv="Content-Type" content="text/html; charset=ISO-8859-1" />				<!-- Link to 960.css stylesheet & main.css stylesheet --> 			<link rel="stylesheet" href="css/960_16.css" type="text/css" />			<link href="css/main.css" rel="stylesheet" type="text/css" />					<!-- Import JQuery -->			<script src="http://code.jquery.com/jquery-1.7.1.min.js"></script>						<!-- Remember to Change Title! -->			<title>HWATS - Weight: Decisional Balance</title>					<!-- JavaScript -->		<script type="text/javascript">			function validateForm()			{				var error = 0;				var isSelected;				var numberRadio = document.getElementById('radioCount').value; //101				var numberQuestion = document.getElementById('questionCount').value; //21				var numberAnswer = document.getElementById('answerCount').value; //6				var countAnswer;				var testValue;				var countButton = 1;				var tmpValue;				var countQuestion = 1;				var focusString = -1;				//go through each question				while(countQuestion < numberQuestion)				{					//set the answer counter and reset the selected flag					isSelected = 0;					countAnswer = 1;					//go through each radio button					while(countAnswer < numberAnswer)					{						//if the button is selected, this question is answerd						if(document.getElementById(countButton).checked == true)						{							isSelected = 1;						}							//Increase the counters for answer and button						countAnswer++;						countButton++;					}										//set the ID name 					questionElement = "question" + countQuestion;					errorElement = "error" + countQuestion;															//if the question is not answer, mark the question with * and raise the error flag					if(isSelected < 1)					{							//set the focus string, if not set						if(focusString < 0)							focusString = (numberAnswer - 1)*(countQuestion - 1)+1;						error = 1;						document.getElementById(errorElement).innerHTML="<big>* </big>";					}					else					{						//else make it black						document.getElementById(errorElement).innerHTML="";					}					countQuestion++;					}				//if there is an error, focus on the top most error question and return false (page not submitted)				if(error > 0)				{					document.getElementById(focusString).focus();					return false;				}				//return true (page submitted)				return true;					}		</script>	</head>	<body>		<div class="site"><!-- SITE CONTAINER -->			<!-- PAGE HEADER --><?php include "includes/header.php"; ?><!-- END PAGE HEADER -->			<!-- PAGE CONTENT -->			<?php												//if the partid is set, it is an update. else, it is an insert				if(!isset($_POST['partid']))					$partid = 0;				else					$partid = $_POST['partid'];								//if partid is set, grab the answers from the wdbreults table				if($partid != 0)				{					$sql_wdbanswer = "select WDBANSWERID from wdbresults where participantid = ".$partid." ORDER BY WDBQUESTIONID";					$result_wdbanswer = mysql_query($sql_wdbanswer);									}			?>			<h1>Weight: Decisional Balance</h1>			<p>Each statement represents a thought that might occur to a person who is deciding whether or not to lose weight. Please indicate how <b>IMPORTANT</b> each of these statements might be to you if you were considering a decision to lose weight. There are <b>FIVE</b> possible responses to each of the items that reflect your answer to the question "How important would this be to you?" Please select the answer that best describes how important each statement would be to you if you were deciding whether or not to lose weight.</p>			<div class='clear'>&nbsp;</div>					<br /><br />						<div class='clear'>&nbsp;</div>			<b>PLEASE READ EACH STATEMENT AND SELECT THE ANSWER ON THE <u>RIGHT</u> TO INDICATE HOW YOU RATE ITS LEVEL OF IMPORTANCE AS IT RELATES TO YOUR MAKING A DECISION ABOUT WHETHER TO LOSE WEIGHT.</b>			<div class='clear'>&nbsp;</div>			<form action='post/wdbformpost.php' method='post' id='myform' onsubmit="return validateForm()">				<div class="container container_16">					<div class='grid_6' align="center">How important is this to me?</div>					<div class='grid_10' align="center">Importance in making a decision about losing weight:</div>					<hr />					<div class='clear'>&nbsp;</div>								<?php						// get the questions						$sql_questions = 'select * from wdbquestions';						$result_questions = mysql_query($sql_questions);						//set the query for answers						$sql_answers = 'SELECT * FROM wdbanswer';										$i = 1;						$j = 1;						$k = 1;						while($row_questions = mysql_fetch_array($result_questions))						{							//Grab the question and their ID							$questionid = $row_questions['WDBQUESTIONID'];							$questiondesc = $row_questions['WDBQUESTIONDESC'];														//set the background when it is an odd numebr														echo '<div class="grid_16 padding"';							if($i % 2 != 0)							{								echo ' style="background-color:#e1e1e1"';							}														echo '>';														//display the question							echo '<div class="grid_5 alpha" id="question'.$i.'"><b id="error'.$i.'" style="color:red"></b>'.$i.'.) '.$questiondesc.'</div>';							//run query for answers							$result_answers = mysql_query($sql_answers);							$k = 1;														//if it is an update, grab the next answer							if($partid != 0)							{																	$row_wdbanswer = mysql_fetch_row($result_wdbanswer);								$wdbresult = $row_wdbanswer[0];							}							else							{								$wdbresult = 0;							}														while($row_answers = mysql_fetch_array($result_answers))							{								//Grab the answer and their ID								$answerid = $row_answers['WDBANSWERID'];								$answerdesc = $row_answers['WDBANSWERDESC'];								//set the grid size								if($k == 3)								{									$gridsize = 'grid_3';								}								else if($k == 5)								{									$gridsize = 'grid_2 omega';								}								else								{									$gridsize = 'grid_2';								}																//if the result is equal to the current answer, set it checked								if($wdbresult == $answerid)								{									$selectText = " checked='true' ";								}								else								{									$selectText = " ";								}																	//Display the answer as a radio button								echo "<div class='".$gridsize."'><input".$selectText."type='radio' name='group".$questionid."' value='".$answerid."' id='".$j."' >".$answerdesc."</div>";								$j++;								$k++;							}							//New line							echo "</div>";							echo "<div class='clear'>&nbsp;</div>";							$i++;														}						echo "<div class='buttonarea'>";						echo '<input type="reset" value="Clear" />';						echo '&nbsp;';						echo '<input type="submit" value="Submit" />';						echo '</div></div>';						echo '</form>';						//store the number of questions, radioBttuons, and answers (plus 1) to be used for validations						echo '<input type="hidden" id="questionCount" value="'.$i.'" style="display:none" />';						echo '<input type="hidden" id="radioCount" value="'.$j.'" style="display:none" />';						echo '<input type="hidden" id="answerCount" value="'.$k.'" style="display:none" />';					?>										<!--				</div>			</form>		-->												<!-- END PAGE CONTENT -->					</div><!-- END SITE CONTAINER -->		<!-- PAGE FOOTER --><?php include "includes/footer.php"; ?><!-- END PAGE FOOTER -->	</body></html>